        function createDoor(
            groupId, 
            groupName, 
            doorHeight, 
            doorWidth, 
            doorName, 
            groupPositionX, 
            groupPositionY, 
            handleName,
            handlePosTop, 
            handlePosSides
        ) {
            self.scene.remove(groupName);
        
            groupName = new THREE.Group();
            groupName.name = groupId;
        
            groupName.add(self.createDoorProto(doorHeight + 2, doorWidth, doorName, doorName));
        
            groupName.position.x = groupPositionX;
            groupName.position.y = groupPositionY;
            groupName.position.z = -self.options.room.width / 2 + self.options.shelf.width - (self.options.shelf.thickness);
        
            /* var texture = groupName.getObjectByName(doorName).material.map;
            texture.repeat.x = self.options.shelf.length / self.boardTextureStep * self.boardTextureRepeatXPerStep;  
            texture.repeat.y = self.options.shelf.width / self.boardTextureStep * self.boardTextureRepeatYPerStep;
            groupName.getObjectByName(doorName).material.needsUpdate = true; */
            
            self.loadAndPlaceHandle(handleName, handlePosTop,handlePosSides, self.options.shelf.width + 5, groupName);
            self.scene.add(groupName);
        };


