    createFloor: function() {
        var self = this;
        var geometry = new THREE.PlaneGeometry(this.roomSizeMin, this.roomSizeMin);
        var material = new THREE.MeshBasicMaterial({color: 0xE7E7E7, side: THREE.DoubleSide, emissive : 0xE7E7E7});
        var floor = new THREE.Mesh(geometry, material);
        floor.position.y = -1 //1.8def
        floor.position.x = 78.63
        //floor.position.z = -103.79
        floor.position.z = -120

        floor.castShadow = true
        floor.receiveShadow = true

        floor.material.side = THREE.DoubleSide;

        floor.scale.x = this.options.room.length / this.roomSizeMin * 3;
        floor.scale.y = this.options.room.width / this.roomSizeMin * 3;
        floor.rotation.x = -Utils.PI_HALF;
        
        this.textureLoader.load(this.assetsPath + this.options.floor.texture, function(texture) {

            floor.material.needsUpdate = true;
            texture.wrapS = texture.wrapT = THREE.RepeatWrapping;
            texture.repeat.x = self.options.room.length / self.roomSizeTextureStep * self.floorTextureRepeatXPerStep * 2

            texture.repeat.y = self.options.room.width / self.roomSizeTextureStep * self.floorTextureRepeatYPerStep * 2

            texture.magFilter = THREE.LinearMipmapLinearFilter

            floor.material.map = texture;
            
        });
        
        floor.name = 'floor';
        floor.visible = true;
        this.floor = floor;
        //self.raycastgroup.push(floor);
        this.scene.add(floor);
    },


