            function requestChangeShelving4_3 () { 
                
                var max = parseInt(this.max);
                if (parseInt(this.value) >= max) {
                    this.value = max; 
                }
    
                var min = parseInt(this.min);
                if (parseInt(this.value) <= min) {
                    this.value = min; 
                }
    
                createInternalShelving_sect4_3();
            };
    
    
